import Slider from 'react-slick';
import { useState } from 'react';
import ReactFlagsSelect from 'react-flags-select';

export default function HeroSection(){

    const [selected, setSelected] = useState('US');

    const phones  = {
      US :'+1',
      DE : '+50',
      TR : '+90',
      IT : '+20'
    }

    const settings = {
        dots: false,
        infinite: true,
        arrows : false,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1
      };

    return(
        <div className="relative h-[500] before:bg-gradient-to-r before:from-primary-brand-color before:to-transparent before:absolute before:inset-0 before:w-full before:h-full before:z-10">
          <Slider {...settings}>
          <div>
            <img className="w-full h-[500px] object-cover" src="https://getir.com/_next/static/images/getir-mainpage-1-757eca6a46304def60cabce74d3f20a2.jpg" alt="" />
          </div>
          <div>
            <img className="w-full h-[500px] object-cover" src="https://getir.com/_next/static/images/getir-mainpage-4-1751ad2d8fb42a88742d6751938da7e7.jpg" alt="" />
          </div>
          </Slider>
          <div className="container flex justify-between items-center absolute top-0 left-1/2 -translate-x-1/2 h-full z-20">
            <div>
              <img src="https://getir.com/_next/static/images/bimutluluk-b3a7fcb14fc9a9c09b60d7dc9b1b8fd6.svg" alt="" />
              <h3 className="mt-8 text-4xl font-semibold text-white">
              Dakikalar içinde <br/> kapınızda
              </h3>
            </div>
            <div className="w-[400px] rounded-lg bg-gray-50 p-6">
              <h4 className="text-primary-brand-color text-center font-semibold ">Giriş yap veya kayıt ol</h4>
              <div className="flex">
              <ReactFlagsSelect
                countries={["US", "GB", "FR","DE","IT"]}
                customLabels={{"US": "EN-US","GB": "EN-GB","FR": "FR","DE": "DE","IT": "IT"}}
                placeholder="Select Language" 
                onSelect={code => setSelected(code)}
                selected={selected}
              />
              </div>
            </div>
          </div>
        </div>
    )
}
