import Slider from 'react-slick';
import Banner from 'api/banners.json'; 
import { useState,useEffect } from 'react';

export default function Campaigns(){

    const [banner,setBanners] = useState([]);

    useEffect(() => {
        setBanners(Banner)
    },[])

    const settings = {
        dots: false,
        infinite: true,
        arrows : false,
        autoplay: true,
        speed: 1000,
        slidesToShow: 3,
        slidesToScroll: 1
      };

    return(
        <div className="container mx-auto">
          <Slider {...settings}>
            {setBanners.length}
          </Slider>
        </div>
    )
}
