import Slider from 'react-slick';
import Banner from 'api/banners.json'; 


export default function Campaigns(){

    const settings = {
        dots: false,
        infinite: true,
        arrows : false,
        autoplay: true,
        speed: 1000,
        slidesToShow: 3,
        slidesToScroll: 1
      };

    return(
        <div className="container mx-auto">
          <Slider {...settings}>
          <div>
            <img className="w-full  object-cover" src="https://getir.com/_next/static/images/getir-mainpage-1-757eca6a46304def60cabce74d3f20a2.jpg" alt="" />
          </div>
          <div>
            <img className="w-full object-cover" src="https://getir.com/_next/static/images/getir-mainpage-4-1751ad2d8fb42a88742d6751938da7e7.jpg" alt="" />
          </div>
          <div>
            <img className="w-full object-cover" src="https://getir.com/_next/static/images/getir-mainpage-4-1751ad2d8fb42a88742d6751938da7e7.jpg" alt="" />
          </div>
          </Slider>
        </div>
    )
}
